// src/components/TaskInput.js
import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { addTask } from '../redux/actions/taskActions';
import { TextField, Button, Typography } from '@material-ui/core';
import axios from 'axios';

const TaskInput = () => {
    const [task, setTask] = useState('');
    const [priority, setPriority] = useState('Low');
    const [weather, setWeather] = useState(null);
    const dispatch = useDispatch();

    useEffect(() => {
        const fetchWeather = async () => {
            try {
                const response = await axios.get('https://api.openweathermap.org/data/2.5/weather?q=London&appid=YOUR_API_KEY');
                setWeather(response.data);
            } catch (error) {
                console.error('Error fetching the weather data', error);
            }
        };

        fetchWeather();
    }, []);

    const handleAddTask = () => {
        if (task.trim() !== '') {
            dispatch(addTask({ task, priority }));
            setTask('');
        }
    };

    return (
        <div>
            <TextField
                label="Task"
                value={task}
                onChange={(e) => setTask(e.target.value)}
                variant="outlined"
            />
            <TextField
                label="Priority"
                value={priority}
                onChange={(e) => setPriority(e.target.value)}
                variant="outlined"
                select
                SelectProps={{
                    native: true,
                }}
            >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
            </TextField>
            <Button onClick={handleAddTask} variant="contained" color="primary">
                Add Task
            </Button>
            {weather && (
                <Typography variant="body1" color="textSecondary">
                    Current weather in {weather.name}: {weather.weather[0].description}, {Math.round(weather.main.temp - 273.15)}Â°C
                </Typography>
            )}
        </div>
    );
};

export default TaskInput;
