import React from 'react';
import { Container, Navbar, Nav, Button } from 'react-bootstrap';
import TaskInput from './components/TaskInput';
import TaskList from './components/TaskList';
import { useSelector, useDispatch } from 'react-redux';
import { loginUser, logoutUser } from './redux/actions/userActions';

const App = () => {
  const isAuthenticated = useSelector(state => state.isAuthenticated);
  const dispatch = useDispatch();

  const handleLogin = () => {
    dispatch(loginUser({ username: 'user', password: 'pass' })); // Mock credentials
  };

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <div>
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand href="#home">To-Do App</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#tasks">Tasks</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Container>
        {isAuthenticated ? (
          <>
            <TaskInput />
            <TaskList />
            <Button variant="secondary" className="mt-3" onClick={handleLogout}>
              Logout
            </Button>
          </>
        ) : (
          <div className="text-center mt-5">
            <h4>Please log in to see your tasks</h4>
            <Button variant="primary" onClick={handleLogin}>
              Login
            </Button>
          </div>
        )}
      </Container>
    </div>
  );
};

export default App;
