import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { deleteTask } from '../redux/actions/taskActions';
import { ListGroup, Button } from 'react-bootstrap';

const TaskList = () => {
  const tasks = useSelector((state) => state.tasks.tasks); // Adjust if necessary
  const dispatch = useDispatch();

  const handleDelete = (id) => {
    dispatch(deleteTask(id));
  };

  return (
    <ListGroup>
      {Array.isArray(tasks) ? (
        tasks.map(task => (
          <ListGroup.Item key={task.id}>
            {task.text} - <span className={`badge bg-${task.priority === 'High' ? 'danger' : task.priority === 'Medium' ? 'warning' : 'success'}`}>{task.priority}</span>
            <Button variant="danger" className="float-end" onClick={() => handleDelete(task.id)}>Delete</Button>
          </ListGroup.Item>
        ))
      ) : (
        <ListGroup.Item>No tasks available</ListGroup.Item>
      )}
    </ListGroup>
  );
};

export default TaskList;
