export const ADD_TASK = 'ADD_TASK';
export const DELETE_TASK = 'DELETE_TASK';
export const UPDATE_TASK = 'UPDATE_TASK';

export const addTask = (name) => ({
  type: ADD_TASK,
  payload: { name },
});

export const deleteTask = (id) => ({
  type: DELETE_TASK,
  payload: id,
});

export const updateTask = (id, name) => ({
  type: UPDATE_TASK,
  payload: { id, name },
});
