import { ADD_TASK, DELETE_TASK, FETCH_WEATHER, SET_ERROR } from '../actionTypes';

export const addTask = (task) => ({
  type: ADD_TASK,
  payload: task
});

export const deleteTask = (id) => ({
  type: DELETE_TASK,
  payload: id
});

export const fetchWeather = (weather) => ({
  type: FETCH_WEATHER,
  payload: weather
});

export const setError = (error) => ({
  type: SET_ERROR,
  payload: error
});
