// src/redux/reducers/userReducer.js
const initialState = {
    username: null,
  };
  
  const userReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'LOGIN':
        return { ...state, username: action.payload };
      case 'LOGOUT':
        return { ...state, username: null };
      default:
        return state;
    }
  };
  
  export default userReducer;
