// src/redux/reducers/index.js
import { combineReducers } from 'redux';
import userReducer from './userReducer'; // Make sure this file exists
import taskReducer from './taskReducer'; // Make sure this file exists

const rootReducer = combineReducers({
  user: userReducer,
  tasks: taskReducer
});

export default rootReducer;
